package bitmap

import (
	"encoding/hex"
	"os"
	"testing"
)

func TestBitMap_LoadRLE(t *testing.T) {
	s := ""
	data, _ := hex.DecodeString(s)

	bitmap := NewBitmapFromRLE(&Option{
		Width: 64, Height: 64, BitPerPixel: 16, Data: data,
	})

	pngData := bitmap.ToPng()
	_ = os.WriteFile("./rle.png", pngData, 0644)
}

